Write-Host "==== TESTE LOGIN FRONTEND ====" -ForegroundColor Cyan; $loginPayload = @{ email = "admin@test.com"; password = "test123" } | ConvertTo-Json; Write-Host "Payload: $loginPayload" -ForegroundColor Gray; try { $response = Invoke-WebRequest -Uri "http://localhost:8000/auth/login-multitenant" -Method POST -Headers @{"Content-Type"="application/json"} -Body $loginPayload -UseBasicParsing; Write-Host "STATUS: $($response.StatusCode)" -ForegroundColor Green; $data = $response.Content | ConvertFrom-Json; Write-Host "Token: $($data.access_token.Substring(0,30))..." -ForegroundColor Cyan; Write-Host "Tenants: $($data.tenants.Count)" -ForegroundColor Cyan; Write-Host "LOGIN OK! O problema esta no frontend." -ForegroundColor Green } catch { Write-Host "ERRO: $($_.Exception.Message)" -ForegroundColor Red }
